import{_ as o,c as t,ag as a,o as i}from"./chunks/framework.L8ryr_Zo.js";const p=JSON.parse('{"title":"Cookie Policy","description":"","frontmatter":{"sidebar":false,"editLink":false,"outline":false,"prev":false,"next":false},"headers":[],"relativePath":"cookie-policy.md","filePath":"cookie-policy.md"}'),s={name:"cookie-policy.md"};function r(n,e,c,l,d,h){return i(),t("div",null,e[0]||(e[0]=[a('<h1 id="cookie-policy" tabindex="-1">Cookie Policy <a class="header-anchor" href="#cookie-policy" aria-label="Permalink to &quot;Cookie Policy&quot;">​</a></h1><p><em>Last updated: July 12, 2025</em></p><p>This website uses cookies to enhance your browsing experience and to collect anonymous analytics data. This policy explains what cookies are, which ones we use, and how you can manage your preferences.</p><h2 id="_1-what-are-cookies" tabindex="-1">1. What are cookies? <a class="header-anchor" href="#_1-what-are-cookies" aria-label="Permalink to &quot;1. What are cookies?&quot;">​</a></h2><p>Cookies are small text files stored on your device when you visit a website. They allow websites to remember your actions and preferences (such as language or session settings) over a period of time.</p><p>Cookies can be:</p><ul><li><strong>First-party cookies</strong> – set by the website you are visiting</li><li><strong>Third-party cookies</strong> – set by an external provider (like Google Analytics)</li></ul><p>They can also be:</p><ul><li><strong>Session cookies</strong> – deleted when you close your browser</li><li><strong>Persistent cookies</strong> – stored for a defined period</li></ul><h2 id="_2-how-we-use-cookies" tabindex="-1">2. How we use cookies <a class="header-anchor" href="#_2-how-we-use-cookies" aria-label="Permalink to &quot;2. How we use cookies&quot;">​</a></h2><p>This site uses only the following types of cookies:</p><h3 id="strictly-necessary-cookies" tabindex="-1">Strictly necessary cookies <a class="header-anchor" href="#strictly-necessary-cookies" aria-label="Permalink to &quot;Strictly necessary cookies&quot;">​</a></h3><p>These cookies are required for basic functionality, such as remembering your cookie preferences. They do not track you or store any personally identifiable information.</p><table tabindex="0"><thead><tr><th>Name</th><th>Domain</th><th>Expiration</th><th>Description</th></tr></thead><tbody><tr><td>cc_cookie</td><td>validator.axe-api.com</td><td>6 months</td><td>Remembers your cookie preferences.</td></tr></tbody></table><h3 id="performance-and-analytics-cookies" tabindex="-1">Performance and analytics cookies <a class="header-anchor" href="#performance-and-analytics-cookies" aria-label="Permalink to &quot;Performance and analytics cookies&quot;">​</a></h3><p>These cookies help us understand how visitors use the site so we can improve it. All data collected is anonymized. These cookies are set <strong>only if you accept analytics cookies</strong> via the cookie banner.</p><table tabindex="0"><thead><tr><th>Name</th><th>Domain</th><th>Expiration</th><th>Description</th></tr></thead><tbody><tr><td>^_ga</td><td>google.com</td><td>2 years</td><td>Used to distinguish users by assigning a unique client ID.</td></tr><tr><td>^<em>ga</em></td><td>google.com</td><td>2 years</td><td>Used by Google Analytics 4 to persist session state for a specific property.</td></tr><tr><td>^_gid</td><td>google.com</td><td>24 hours</td><td>Used to distinguish users and track user behavior for 24 hours.</td></tr><tr><td>^_gat</td><td>google.com</td><td>1 minute</td><td>Used to throttle request rate to Google Analytics servers.</td></tr></tbody></table><p>All Google Analytics cookies are configured to <strong>anonymize IP addresses</strong>.</p><p>For more information, see <a href="https://developers.google.com/analytics/devguides/collection/gtagjs/cookie-usage" target="_blank" rel="noreferrer">Google’s cookie usage documentation</a>.</p><h2 id="_3-how-to-manage-your-preferences" tabindex="-1">3. How to manage your preferences <a class="header-anchor" href="#_3-how-to-manage-your-preferences" aria-label="Permalink to &quot;3. How to manage your preferences&quot;">​</a></h2><p>You can change or withdraw your cookie consent at any time by clicking the <strong>“Cookie preferences”</strong> link in the footer of the site.</p><p>You can also delete or block cookies via your browser settings. For guidance on how to do this, visit <a href="https://www.aboutcookies.org/" target="_blank" rel="noreferrer">www.aboutcookies.org</a>.</p><p>Please note that blocking certain cookies may affect the functionality of the site.</p><h2 id="_4-changes-to-this-policy" tabindex="-1">4. Changes to this policy <a class="header-anchor" href="#_4-changes-to-this-policy" aria-label="Permalink to &quot;4. Changes to this policy&quot;">​</a></h2><p>We may update this Cookie Policy from time to time. Changes will be posted on this page with an updated revision date.</p><hr><p>If you have any questions about our use of cookies, feel free to contact us at <a href="mailto:i.ozguradem@gmail.com" target="_blank" rel="noreferrer">i.ozguradem@gmail.com</a>.</p>',27)]))}const y=o(s,[["render",r]]);export{p as __pageData,y as default};
